-- Update user_male.png to male base64 avatar
UPDATE users 
SET avatar_url = 'data:image/jpeg;base64,'
WHERE avatar_url = 'user_male.png';

-- Update user_female.png to female base64 avatar  
UPDATE users
SET avatar_url = 'data:image/jpeg;base64,'
WHERE avatar_url = 'user_female.png';

-- Update user.png to unknown base64 avatar  
UPDATE users
SET avatar_url = 'data:image/jpeg;base64,'
WHERE avatar_url = 'user.png';