-- V8: Update novel images with random base URLs
-- This migration updates 200 existing novels with random images from 5 base URLs

-- Update 200 novels with random images from the 5 base URLs
-- Using ROW_NUMBER() to ensure we get exactly 200 novels
WITH novel_updates AS (
    SELECT 
        id,
        ROW_NUMBER() OVER (ORDER BY random()) as rn
    FROM novel
    WHERE is_valid = true
    ORDER BY random()
    LIMIT 200
)
UPDATE novel 
SET 
    cover_img_url = CASE 
        WHEN FLOOR(random() * 5) = 0 THEN 'data:image/jpeg;base64,'
        WHEN FLOOR(random() * 5) = 1 THEN 'data:image/jpeg;base64,'
        WHEN FLOOR(random() * 5) = 2 THEN 'data:image/jpeg;base64,'
        WHEN FLOOR(random() * 5) = 3 THEN 'data:image/jpeg;base64,'
        WHEN FLOOR(random() * 5) = 4 THEN 'data:image/jpeg;base64,'
    END,
    update_time = CURRENT_TIMESTAMP
FROM novel_updates nu
WHERE novel.id = nu.id;
