choose.when:
SELECT 
    DATE(create_time) as date,
    TO_CHAR(DATE(create_time), 'YYYY-MM-DD') as periodLabel,
    COUNT(*) as count,
    LAG(COUNT(*)) OVER (ORDER BY DATE(create_time)) as previousCount
FROM novel 
WHERE create_time BETWEEN #{startDate} AND #{endDate}

    AND category_id = #{categoryId}


    AND author_id = #{authorId}


    AND status = #{status}

GROUP BY DATE(create_time)
ORDER BY DATE(create_time)

SELECT 
    DATE_TRUNC('week', create_time) as date,
    TO_CHAR(DATE_TRUNC('week', create_time), 'YYYY-"W"WW') as periodLabel,
    COUNT(*) as count,
    LAG(COUNT(*)) OVER (ORDER BY DATE_TRUNC('week', create_time)) as previousCount
FROM novel 
WHERE create_time BETWEEN #{startDate} AND #{endDate}

    AND category_id = #{categoryId}


    AND author_id = #{authorId}


    AND status = #{status}

GROUP BY DATE_TRUNC('week', create_time)
ORDER BY DATE_TRUNC('week', create_time)

SELECT 
    DATE_TRUNC('month', create_time) as date,
    TO_CHAR(DATE_TRUNC('month', create_time), 'YYYY-MM') as periodLabel,
    COUNT(*) as count,
    LAG(COUNT(*)) OVER (ORDER BY DATE_TRUNC('month', create_time)) as previousCount
FROM novel 
WHERE create_time BETWEEN #{startDate} AND #{endDate}

    AND category_id = #{categoryId}


    AND author_id = #{authorId}


    AND status = #{status}

GROUP BY DATE_TRUNC('month', create_time)
ORDER BY DATE_TRUNC('month', create_time)

WITH novel_activity AS ( SELECT DATE(n.create_time) as activity_date, SUM(n.view_cnt) as views, COUNT(DISTINCT h.id) as chapters_read FROM novel n LEFT JOIN history h ON n.id = h.novel_id WHERE n.create_time BETWEEN #{startDate} AND #{endDate}
AND n.category_id = #{categoryId}
AND n.author_id = #{authorId}
GROUP BY DATE(n.create_time) ), comment_activity AS ( SELECT DATE(c.create_time) as activity_date, COUNT(*) as comments FROM comment c LEFT JOIN chapter ch ON c.chapter_id = ch.id LEFT JOIN novel n ON ch.novel_id = n.id WHERE c.create_time BETWEEN #{startDate} AND #{endDate}
AND n.category_id = #{categoryId}
AND n.author_id = #{authorId}
GROUP BY DATE(c.create_time) ), review_activity AS ( SELECT DATE(r.create_time) as activity_date, COUNT(*) as reviews FROM review r LEFT JOIN novel n ON r.novel_id = n.id WHERE r.create_time BETWEEN #{startDate} AND #{endDate}
AND n.category_id = #{categoryId}
AND n.author_id = #{authorId}
GROUP BY DATE(r.create_time) ), vote_activity AS ( SELECT DATE(v.create_time) as activity_date, COUNT(*) as votes FROM vote v LEFT JOIN novel n ON v.novel_id = n.id WHERE v.create_time BETWEEN #{startDate} AND #{endDate}
AND n.category_id = #{categoryId}
AND n.author_id = #{authorId}
GROUP BY DATE(v.create_time) ) SELECT na.activity_date as date, TO_CHAR(na.activity_date, 'YYYY-MM-DD') as periodLabel, COALESCE(na.views, 0) as views, COALESCE(na.chapters_read, 0) as chaptersRead, COALESCE(ca.comments, 0) as comments, COALESCE(ra.reviews, 0) as reviews, COALESCE(va.votes, 0) as votes FROM novel_activity na LEFT JOIN comment_activity ca ON na.activity_date = ca.activity_date LEFT JOIN review_activity ra ON na.activity_date = ra.activity_date LEFT JOIN vote_activity va ON na.activity_date = va.activity_date ORDER BY na.activity_date

WITH novel_activity AS ( SELECT DATE_TRUNC('week', n.create_time) as activity_date, SUM(n.view_cnt) as views, COUNT(DISTINCT h.id) as chapters_read FROM novel n LEFT JOIN history h ON n.id = h.novel_id WHERE n.create_time BETWEEN #{startDate} AND #{endDate}
AND n.category_id = #{categoryId}
AND n.author_id = #{authorId}
GROUP BY DATE_TRUNC('week', n.create_time) ), comment_activity AS ( SELECT DATE_TRUNC('week', c.create_time) as activity_date, COUNT(*) as comments FROM comment c LEFT JOIN chapter ch ON c.chapter_id = ch.id LEFT JOIN novel n ON ch.novel_id = n.id WHERE c.create_time BETWEEN #{startDate} AND #{endDate}
AND n.category_id = #{categoryId}
AND n.author_id = #{authorId}
GROUP BY DATE_TRUNC('week', c.create_time) ), review_activity AS ( SELECT DATE_TRUNC('week', r.create_time) as activity_date, COUNT(*) as reviews FROM review r LEFT JOIN novel n ON r.novel_id = n.id WHERE r.create_time BETWEEN #{startDate} AND #{endDate}
AND n.category_id = #{categoryId}
AND n.author_id = #{authorId}
GROUP BY DATE_TRUNC('week', r.create_time) ), vote_activity AS ( SELECT DATE_TRUNC('week', v.create_time) as activity_date, COUNT(*) as votes FROM vote v LEFT JOIN novel n ON v.novel_id = n.id WHERE v.create_time BETWEEN #{startDate} AND #{endDate}
AND n.category_id = #{categoryId}
AND n.author_id = #{authorId}
GROUP BY DATE_TRUNC('week', v.create_time) ) SELECT na.activity_date as date, TO_CHAR(na.activity_date, 'YYYY-"W"WW') as periodLabel, COALESCE(na.views, 0) as views, COALESCE(na.chapters_read, 0) as chaptersRead, COALESCE(ca.comments, 0) as comments, COALESCE(ra.reviews, 0) as reviews, COALESCE(va.votes, 0) as votes FROM novel_activity na LEFT JOIN comment_activity ca ON na.activity_date = ca.activity_date LEFT JOIN review_activity ra ON na.activity_date = ra.activity_date LEFT JOIN vote_activity va ON na.activity_date = va.activity_date ORDER BY na.activity_date

WITH novel_activity AS ( SELECT DATE_TRUNC('month', n.create_time) as activity_date, SUM(n.view_cnt) as views, COUNT(DISTINCT h.id) as chapters_read FROM novel n LEFT JOIN history h ON n.id = h.novel_id WHERE n.create_time BETWEEN #{startDate} AND #{endDate}
