GROUP BY DATE_TRUNC('month', c.create_time) ), review_activity AS ( SELECT DATE_TRUNC('month', r.create_time) as activity_date, COUNT(*) as reviews FROM review r LEFT JOIN novel n ON r.novel_id = n.id WHERE r.create_time BETWEEN #{startDate} AND #{endDate}
AND n.category_id = #{categoryId}
