mapper.select:
select 

from vote
where user_id = #{userId,jdbcType=OTHER} and novel_id = #{novelId,jdbcType=INTEGER}

mapper.update:
update vote
set is_active = false, update_time = CURRENT_TIMESTAMP
where user_id = #{userId,jdbcType=OTHER} and novel_id = #{novelId,jdbcType=INTEGER}

update vote
set is_active = true, update_time = CURRENT_TIMESTAMP
where user_id = #{userId,jdbcType=OTHER} and novel_id = #{novelId,jdbcType=INTEGER}
